package com.sh.sep27.main;

import java.io.IOException;

import javax.servlet.RequestDispatcher;
import javax.servlet.ServletException;
import javax.servlet.annotation.WebServlet;
import javax.servlet.http.HttpServlet;
import javax.servlet.http.HttpServletRequest;
import javax.servlet.http.HttpServletResponse;

// Controller
//		JSP환경에서 상황판단
//			(GET / POST) 구분에 Servlet이 적합
//				계산을 할 때 M(Model)을 이용해 코드 작성
//				출력해야 한다면 V(View)로
//		 => 흐름제어

@WebServlet("/FirstC") // FirstC를 Tomcat에 등록
public class FirstC extends HttpServlet {
	
	// 이 프로젝트에는 main method 부재
	// Tomcat에 main method 내장 -> 자동으로 FirtstC객체를 만들어 실행
	// 이 프로젝트가 처음 시작할 때 생성자에 의해 객체가 생성
	public FirstC() { // 기본생성자, 없으면 자동으로 만들어줌
		super();
		// 처음실행시 사과 데이터 총 개수를 받아오는 역할
	}
	
	// 웹사이트의 페이지 전환 : C로 요청 -> M에서 기능 작업 -> V에서 출력 (forwarding)...............................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................
	protected void doGet(HttpServletRequest request, HttpServletResponse response)
			throws ServletException, IOException {
		// 사이트에 처음 진입 시
		
		// FirstC에 GET방식 요청 (요청 파라미터 없이)
		if (!request.getParameterNames().hasMoreElements()) {

			// firstV.jsp로 현재 발생한 요청을 제보해주는 객체
			RequestDispatcher rd = request.getRequestDispatcher("firstV.jsp");
			rd.forward(request, response); // firstV.jsp를 페이지 이동 중에 forwarding
		}
	}

	protected void doPost(HttpServletRequest request, HttpServletResponse response)
			throws ServletException, IOException {
	
	}

}




























