package com.heim.oct24.contents;

public class Content {
	private String b_num;
	private String b_title;
	private String b_content;
	private String b_author;
	private String b_date;
	private String b_thumb;
	private String b_img;
	
	public Content() {
		// TODO Auto-generated constructor stub
	}

	public Content(String b_num, String b_title, String b_content, String b_author, String b_date, String b_thumb,
			String b_img) {
		super();
		this.b_num = b_num;
		this.b_title = b_title;
		this.b_content = b_content;
		this.b_author = b_author;
		this.b_date = b_date;
		this.b_thumb = b_thumb;
		this.b_img = b_img;
	}

	public String getB_num() {
		return b_num;
	}

	public void setB_num(String b_num) {
		this.b_num = b_num;
	}

	public String getB_title() {
		return b_title;
	}

	public void setB_title(String b_title) {
		this.b_title = b_title;
	}

	public String getB_content() {
		return b_content;
	}

	public void setB_content(String b_content) {
		this.b_content = b_content;
	}

	public String getB_author() {
		return b_author;
	}

	public void setB_author(String b_author) {
		this.b_author = b_author;
	}

	public String getB_date() {
		return b_date;
	}

	public void setB_date(String b_date) {
		this.b_date = b_date;
	}

	public String getB_thumb() {
		return b_thumb;
	}

	public void setB_thumb(String b_thumb) {
		this.b_thumb = b_thumb;
	}

	public String getB_img() {
		return b_img;
	}

	public void setB_img(String b_img) {
		this.b_img = b_img;
	}
	
	
	
	
	
	
	
}
